script:
frappe.ui.form.on('Event', {
 onload(frm) {
   if (frm.doc.sync_with_caldav) {
	  sync_caldav(frm);    
   }
   frm.fields_dict.minute_item.grid.get_field('consequent_event').get_query = function(doc, cdt, cdn) {
     var child = locals[cdt][cdn];
     //console.log(child);
     return {    
       filters:[
         ['name', '!=', doc.name],
         ['status', '!=', 'Closed']
       ]
     };
   };
   frm.add_custom_button(__("Send Invitation"), function() {
      //console.log(__("Your Code here"));
      Invitations();
	}, __("Invitations"));
 },
 refresh(frm) {
   if (frm.doc.sync_with_caldav) {
	  sync_caldav(frm);    
   }
 },
 sync_with_caldav(frm) {
   if (frm.doc.sync_with_caldav) {
      sync_caldav(frm);
   }
   frm.add_custom_button(__("Send Invitation"), function() {
      //console.log(__("Your Code here"));
      Invitations();
	}, __("Invitations"));
 },
 validate(frm) {
   if (frm.doc.sync_with_caldav) {
     if (frm.doc.caldav_id_calendar === undefined) {
       msgprint(__("Please Select a CalDav Calendar"));
       frappe.validated = false;
     }       
   }     
 }
});
function sync_caldav(frm) {
  if (frappe.session.user != 'Administrator') {
    frappe.call({
      method: "pibidav.pibidav.pibical.get_calendar",
      args: {
        nuser: frappe.session.user,
      },
      callback(r) {
        var data = r.message;
        var options = [];
        if (data.length > 0) {
          for (var i = 0; i < data.length; i++) {
            options.push({
              'label': data[i].name,
              'value': data[i].url
            });
	      }
        }     
        frm.set_df_property("caldav_id_calendar", "options", options);
        frm.set_value("caldav_id_calendar", options[data.length-1].value);
        frm.refresh_field("caldav_id_calendar");
      }
    });
  }      
}
function Invitations() {
  var d = new frappe.ui.Dialog({
    title: __('Invitations'),
	fields: [
	  {
	    fieldtype: 'HTML',
		fieldname: 'text_area'
	  }
	],
	primary_action_label: __('Send Email'),
	primary_action: () => console.log(__('Send Email'))
  });

  d.fields_dict.text_area.$wrapper.html('<div style="height: 100;"><h4>In Progress - Preparing Invitation Communication</h4></div>');
  d.show();
}
